definition.script:
node {
    def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + PARAMETER_UNIQUE_JOB_ID
    def irods_packages_directory = env.JENKINS_OUTPUT + '/' + 'build_irods' + '/' + PARAMETER_UNIQUE_JOB_ID
    if(!PARAMETER_IRODS_PACKAGES_ROOT_DIRECTORY.equals('')) {
        irods_packages_directory = PARAMETER_IRODS_PACKAGES_ROOT_DIRECTORY
    }
    def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
    dir(dockerfile_dir) {
        def build_cmd = 'python build_icommands.py' +
                        ' -p ' + PARAMETER_PLATFORM_TARGET +
                        ' -b ' + PARAMETER_UNIQUE_JOB_ID +
                        ' -o ' + output_directory +
                        ' --repo_directory_identifier ' + PARAMETER_REPO_IDENTIFIER +
                        ' --irods_packages_root_directory ' + irods_packages_directory
        if (!PARAMETER_EXTERNALS_ROOT_DIRECTORY.equals('')) {
            build_cmd = build_cmd + ' --externals_packages_directory ' + PARAMETER_EXTERNALS_ROOT_DIRECTORY
        }
        sh build_cmd
    }
}
