definition.script:
node {
    def unique_id = PARAMETER_UNIQUE_JOB_ID
    if (!PARAMETER_UNIQUE_JOB_ID) {
        unique_id = env.BUILD_ID
    }

    def source_directory = PARAMETER_SOURCE_DIRECTORY

    def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + unique_id

    def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
    dir(dockerfile_dir) {
        def build_cmd = 'python build_irods_on_base_os_image.py' +
                        ' --platform_target ' + PARAMETER_PLATFORM_TARGET +
                        ' --unique_id ' + unique_id +
                        ' --output_directory ' + output_directory
        if (source_directory) {
            build_cmd = build_cmd + ' --source_directory ' + source_directory
        }
        if (PARAMETER_EXTERNALS_ROOT_DIRECTORY) {
            build_cmd = build_cmd + ' --externals_packages_directory ' + PARAMETER_EXTERNALS_ROOT_DIRECTORY
        }
        sh build_cmd
    }
}
