definition.script:
node {
    def output_directory = env.JENKINS_OUTPUT + '/' + env.JOB_NAME + '/' + PARAMETER_UNIQUE_JOB_ID
    stage("Clone Repository") {                                           
        b = build job: "clone_git_repository",                            
            parameters: [                                                 
                [$class: 'StringParameterValue', name: 'PARAMETER_UNIQUE_JOB_ID', value: PARAMETER_UNIQUE_JOB_ID],
                [$class: 'StringParameterValue', name: 'PARAMETER_REPOSITORY', value: PARAMETER_ICOMMANDS_REPO],
                [$class: 'StringParameterValue', name: 'PARAMETER_COMMITISH', value: PARAMETER_ICOMMANDS_COMMITISH],
                [$class: 'StringParameterValue', name: 'PARAMETER_REPO_IDENTIFIER', value: PARAMETER_REPO_IDENTIFIER],
                [$class: 'StringParameterValue', name: 'PARAMETER_IGNORE_CACHE', value: PARAMETER_IGNORE_CACHE]
            ]   
    }   
    def platform_targets = PARAMETER_PLATFORM_TARGETS.split(',')
    def parallelBuilds = [:]
    for(int i = 0; i < platform_targets.size(); i++) { 
        def os = platform_targets[i]
        parallelBuilds["${os}"] = { 
            stage("Build iCommands Packages") { 
                b = build job: "build_icommands",
                    parameters: [ 
                        [$class: 'StringParameterValue', name: 'PARAMETER_PLATFORM_TARGET', value: os],
                        [$class: 'StringParameterValue', name: 'PARAMETER_UNIQUE_JOB_ID', value: PARAMETER_UNIQUE_JOB_ID],
                        [$class: 'StringParameterValue', name: 'PARAMETER_REPO_IDENTIFIER', value: PARAMETER_REPO_IDENTIFIER],
                        [$class: 'StringParameterValue', name: 'PARAMETER_IRODS_PACKAGES_ROOT_DIRECTORY', value: PARAMETER_IRODS_PACKAGES_ROOT_DIRECTORY]
                    ]
            }
        }
    }
    if (parallelBuilds.size() != 0) { 
        parallel(parallelBuilds)
    }
}
