definition.script:
node {
    def unique_id = PARAMETER_UNIQUE_JOB_ID
    if (!PARAMETER_UNIQUE_JOB_ID) {
        unique_id = env.BUILD_ID
    }

    def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
    dir(dockerfile_dir) {
        def clone_cmd = 'python clone_repo.py' +
                            ' --unique_id ' + unique_id +
                            ' --repo ' + PARAMETER_REPOSITORY +
                            ' --commitish ' + PARAMETER_COMMITISH
        if (PARAMETER_CLONE_TARGET_DIRECTORY) {
            clone_cmd = clone_cmd + ' --clone_target_directory ' + PARAMETER_CLONE_TARGET_DIRECTORY
        }
        if (PARAMETER_IGNORE_CACHE == "true") {
            clone_cmd = clone_cmd + ' --ignore_cache'
        }
        sh clone_cmd
    }
}
