definition.script:
node {
    def dockerfile_dir = env.GLOBAL_PARAMETER_DOCKER_REPO
    dir(dockerfile_dir) {
        def clone_cmd = 'python clone_repo.py' +
                            ' -b ' + PARAMETER_UNIQUE_JOB_ID +
                            ' --repo ' + PARAMETER_REPOSITORY +
                            ' --commitish ' + PARAMETER_COMMITISH +
                            ' --repo_directory_identifier ' + PARAMETER_REPO_IDENTIFIER
        if (PARAMETER_IGNORE_CACHE == "true") {
            clone_cmd = clone_cmd + ' --ignore_cache'
        }
        sh clone_cmd
    }
}
